create table if not exists customers (id uuid primary key default gen_random_uuid(), name text, phone text, created_at timestamptz default now()); create table if not exists rates (id uuid primary key default gen_random_uuid(), currency text, buy_rate numeric, sell_rate numeric, created_at timestamptz default now()); create table if not exists ledger (id uuid primary key default gen_random_uuid(), customer_id uuid references customers(id), type text, amount_base numeric, amount_ccy numeric, currency text, rate numeric, created_at timestamptz default now());